#include <iostream>using namespace std;int main () {	int base, height;cout << "Please enter an odd size for the base of the triangle: ";cin >> 	base ;bool valid2 = cin.good();if (valid2){char next2 = cin.get(); bool endOfInt2 = (next2 == ' ') || (next2 == '\n') ;     	      if (!(endOfInt2)) {		  cerr << "Error: Invalid Input. Program Terminated."; 		 return 0;	}	 if (base<0  || base % 2 == 0) 	 {		 cerr << "Error: Invalid Input. Program Terminated."; 		 return 0;		 	 }	  }else {	cerr << "Error: Invalid Input. Program Terminated";	return 0;}height = base / 2 + 1;	for (int i = 1 ; i <= height ; i++) {		for (int j = i - 1; j != 0 ; j--  ){	cout << " ";	}	for (int k = base - 2*(i-1) ; k >= 1 ; k--, base ){	cout << "*"; }		cout << endl;}					return 0;			}